import React, { useState } from "react";
import s from "./menuMobile.module.scss";
import logo2 from "../../assets/beczki/7logo.jpg";
import close from "../../assets/images/svg/icon-close.svg";
import { Link, NavLink } from "react-router-dom";
import { ModalZap } from "../modal/modal";

export const MobileMenu = ({ closeModal }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <>
      <div className={s.bg}>
        <div className={s.firstlane}>
          <Link onClick={closeModal} to="/">
            <img className={s.logo} src={logo2} alt="logo" />
          </Link>

          <img
            className={s.close}
            src={close}
            alt="closebutton"
            onClick={closeModal}
          />
        </div>
        <div>
          <NavLink onClick={closeModal} className={s.features} to="/onas">
            O nas
          </NavLink>

          <NavLink onClick={closeModal} className={s.pricing} to="/transport">
            Transport
          </NavLink>

          <div className={`${s.dropdown} ${isDropdownOpen ? "active" : ""}`}>
            <button onClick={toggleDropdown} className={s.dropdown - btn}>
              Oferta
            </button>
            <div className={s.dropdown - content}>
              <Link onClick={closeModal} to="/oferta/ppoż">
                Zbiorniki ppoż
              </Link>
              <Link onClick={closeModal} to="/oferta/deszczowka">
                Zbiorniki na deszczówkę
              </Link>
              <Link onClick={closeModal} to="/oferta/szambo">
                Zbiorniki na szambo
              </Link>
              <Link onClick={closeModal} to="/oferta/rsm">
                Zbiorniki RSM
              </Link>
            </div>
          </div>

          <NavLink onClick={closeModal} className={s.contact} to="/kontakt">
            Kontakt
          </NavLink>
          <div>
            {isModalOpen ? (
              <ModalZap closeModal={closeModal} />
            ) : (
              <button onClick={openModal} className={s.login}>
                Wyślij zapytanie
              </button>
            )}
          </div>
        </div>
      </div>
    </>
  );
};
